// SPDX-License-Identifier: MIT
pragma solidity >=0.4.22 <0.9.0;

contract CourseMarketplace {
  enum State {
    Purchased,
    Activated,
    Deactivated
  }

  struct Course {
    uint id;
    uint price;
    bytes32 proof;
    address owner;
    State state;
  }

  bool public isStopped = false;

  // mapping of the courseHash to Course data
  mapping(bytes32 => Course) private ownedCourses;

  // mapping of the courseId to courseHash
  mapping(uint => bytes32) private ownedCourseHash;

  // number of all courses + id of the course
  uint private totalOwnedCourses;

  address payable private owner;

  constructor() {
    setContractOwner(msg.sender);
  }

  /// Course has invalid state!
  error InvalidState();


  /// Course does not exist!
  error CourseIsNotCreated();

  /// Course has already a Owner
  error CourseHasOwner();

  /// Sender is not course owner
  error SenderIsNotCourseOwner();

  /// Only owner has the right to set the owner
  error OnlyOwner();

  modifier onlyOwner() {
    // require(msg.sender == owner);
    // _;
    if (msg.sender != getContractOwner()) {
      revert OnlyOwner();
    }
    _;
  }

  modifier onlyWhenNotStopped() {
    require(!isStopped);
    _;
  }

  modifier onlyWhenStopped() {
    require(isStopped);
    _;
  }

  function stopContract() external onlyOwner {
    isStopped = true;
  }

  function resumeContract() external onlyOwner {
    isStopped = false;
  }

  receive() external payable {}

  function withdraw(uint amount) external onlyOwner {
    (bool success, ) = owner.call{value: amount}("");
    require(success, "Withdraw failed");
  }

  function emergencyWithdraw() external onlyWhenStopped onlyOwner {
    (bool success, ) = owner.call{value: address(this).balance}("");
    require(success, "Withdraw failed");
  }

  function selfDestruct() external onlyWhenStopped onlyOwner {
    selfdestruct(owner);
  }

  function purchaseCourse(bytes16 courseId, bytes32 proof) external payable onlyWhenNotStopped {
    bytes32 courseHash = keccak256(abi.encodePacked(courseId, msg.sender));

    if (hasCourseOwnership(courseHash)) {
     revert CourseHasOwner();
    }

    uint id = totalOwnedCourses++;

    ownedCourseHash[id] = courseHash;
    ownedCourses[courseHash] = Course({id: id, price: msg.value, proof: proof, owner: msg.sender, state: State.Purchased});
  }

  function repurchaseCourse(bytes32 courseHash) external payable onlyWhenNotStopped {
    if (!isCourseCreated(courseHash)) { revert CourseIsNotCreated(); }

    if (!hasCourseOwnership(courseHash)) { revert SenderIsNotCourseOwner(); }

    Course storage course = ownedCourses[courseHash];

    if (course.state != State.Deactivated) { revert InvalidState(); }

    course.state = State.Purchased;
    course.price = msg.value;
  }

  function activateCourse(bytes32 courseHash) external onlyWhenNotStopped onlyOwner {
    if (!isCourseCreated(courseHash)) { revert CourseIsNotCreated(); }

    Course storage course = ownedCourses[courseHash];

    if (course.state != State.Purchased) { revert InvalidState(); }

    course.state = State.Activated;
  }

  function deactivateCourse(bytes32 courseHash) external onlyWhenNotStopped onlyOwner {
    if (!isCourseCreated(courseHash)) { revert CourseIsNotCreated(); }

    Course storage course = ownedCourses[courseHash];

    if (course.state != State.Purchased) { revert InvalidState(); }

    (bool success, ) = course.owner.call{value: course.price}("");
    require(success, "Transfer failed");

    course.state = State.Deactivated;
    course.price = 0;
  }

  function transferOwnership(address newOwner) external onlyOwner {
    setContractOwner(newOwner);
  }

  function getCourseCount() external view returns(uint) {
    return totalOwnedCourses;
  }

  function getCourseHash(uint index) external view returns(bytes32) {
    return ownedCourseHash[index];
  }

  function getCourseByHash(bytes32 courseHash) external view returns(Course memory) {
    return ownedCourses[courseHash];
  }

  function getContractOwner() public view returns(address){
    return owner;
  }

  function setContractOwner(address newOwner) private {
    // require(msg.sender == owner);
    owner = payable(newOwner);
  }

  function isCourseCreated(bytes32 courseHash) private view returns (bool) {
    return ownedCourses[courseHash].owner != 0x0000000000000000000000000000000000000000;
  }

  function hasCourseOwnership(bytes32 courseHash) private view returns(bool) {
    return ownedCourses[courseHash].owner == msg.sender;
  }
}
